import React, { useEffect, useState } from "react";
import Notes from "../models/notes";
import { useAuth } from "../provider/authProvider";

const UserNotes = () => {
  const { username } = useAuth();

  const [notes, setNotes] = useState([]);
  const [clicked, setClicked] = useState(false);
  useEffect(() => {
    async function getUserNotes() {
      const allNotes = await Notes.getAllNotes(username);
      setNotes(() => allNotes.data.allNotes);
    }
    getUserNotes();
  }, []);

  const onClick = () => {
    clicked === true ? setClicked(false) : setClicked(true);
  };
  return (
    <>
      <h1>Your Prayer Notes</h1>
      {notes.map((note) => (
        <span>
          <h4 onClick={onClick}>
            {note.date} {note.season} {note.has_prayed ? "üôèüìø" : "üëé"}
          </h4>
          {clicked && <p>note.notes</p>}
        </span>
      ))}
    </>
  );
};

export default UserNotes;
