import axios from "axios";
const API_BASE_URL = "http://localhost:3001";

class Notes {
  static async addNotes(username, notes, has_prayed, date, season) {
    try {
      const res = await axios.post(`${API_BASE_URL}/rosary/notes`, {
        username,
        notes,
        has_prayed,
        date,
        season,
      });
      console.log(res.data);
      return res.data;
    } catch (err) {
      throw err;
    }
  }
  static async getAllNotes(username) {
    try {
      const res = await axios.get(`${API_BASE_URL}/rosary/notes`, {
        username,
      });

      return res.data;
    } catch (err) {
      throw new Error(err);
    }
  }

  static async checkDateExists(username, date) {
    try {
      const res = await axios.get(
        `http://localhost:3001/rosary/notes/check-date`,
        {
          body: {
            username,
            date,
          },
        }
      );

      return res.data.dateExists;
    } catch (err) {
      throw new Error(err);
    }
  }
}

export default Notes;
