import React, { useState, useRef, useEffect } from "react";
import Card from "./Card";
import axios from "axios";

function DeckOfCards() {
  const [cardImgSrc, setCardImgSrc] = useState("");
  const [cardCount, setCardCount] = useState(0);
  const [allCardsUsed, setAllCardsUsed] = useState(false);
  const deckId = useRef();
  useEffect(() => {
    axios
      .get("https://deckofcardsapi.com/api/deck/new/shuffle/")
      .then((res) => (deckId.current = res.data.deck_id));
  }, []);

  const checkCardCount = () => {
    cardCount === 52
      ? setAllCardsUsed(() => true)
      : setAllCardsUsed(() => allCardsUsed);
  };
  const getCard = async () => {
    checkCardCount();
    !allCardsUsed
      ? async () => {
          const res = await axios.get(
            `https://deckofcardsapi.com/api/deck/${deckId.current}/draw/`
          );
          setCardCount(() => cardCount + 1);
          setCardImgSrc(() => res.data.cards[0].image);
        }
      : alert("No cards remaining!");
  };

  return (
    <div>
      <button onClick={getCard}>GIMMIE A CARD</button>
      {cardCount === 53 ? (
        <h1>Thats all the cards!</h1>
      ) : (
        <Card src={cardImgSrc} />
      )}
    </div>
  );
}

export default DeckOfCards;
// https://deckofcardsapi.com/api/deck/new/shuffle/ - get a deck
// https://deckofcardsapi.com/api/deck/<<deck_id>>/draw/ - draw a card
