import React, { useState } from "react";
import SnackOrBoozeApi from "./Api";
import { Redirect, useHistory } from "react-router-dom";
import { Card, CardBody, CardTitle } from "reactstrap";

const AddMenuItemForm = ({ itemType }) => {
  const INITIAL_STATE = {
    id: "",
    name: "",
    description: "",
    recipe: "",
    serve: "",
  };
  const [formData, setFormData] = useState(INITIAL_STATE);

  const history = useHistory();
  //updates state as user inputs data to inputs. Controlled component
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((fData) => ({
      ...fData,
      [name]: value,
    }));
  };
  // adds item to API and redirects back to the previos listing page
  const handleSubmit = async (e) => {
    e.preventDefault();
    await SnackOrBoozeApi.addItem(itemType, formData);
    setFormData(INITIAL_STATE);
    history.push(`/${itemType}`);
  };

  return (
    <section className="col-md-4">
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">
            Add an Item
          </CardTitle>
          <form onSubmit={handleSubmit}>
            <label htmlFor="id">ID</label>
            <input
              type="text"
              id="id"
              name="id"
              value={formData.id}
              onChange={handleChange}
              required
            />
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <label htmlFor="description">Description</label>
            <input
              type="text"
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
            />
            <label htmlFor="recipe">Recipe</label>
            <input
              type="text"
              id="recipe"
              name="recipe"
              value={formData.recipe}
              onChange={handleChange}
              required
            />
            <label htmlFor="serve">Serve</label>
            <input
              type="text"
              id="serve"
              name="serve"
              value={formData.serve}
              onChange={handleChange}
              required
            />
            <button>Add</button>
          </form>
        </CardBody>
      </Card>
    </section>
  );
};

export default AddMenuItemForm;
