import React, { useState, useEffect } from "react";

import Home from "./Home";
import SnackOrBoozeApi from "./Api";
import { Route, Switch } from "react-router-dom";
import Menu from "./BarMenu";
import BarItem from "./BarItem";
import AddMenuItemForm from "./AddMenuItemForm";
import NotFoundPage404 from "./NotFoundPage404";

/**
 * - Contains all routes and state information for snacks and drinks,
 *   aquired by API calls from the getSnacksAndDrinks class
 *
 * - ReRenders when snacks or drinks change in state to accomadate
 *   updates from users adding to the data.
 *
 * -Routes in proper order from most specific onward, rendering a 404
 * page if a page isnt found and redirecting to the snack or drink listing
 * page if a non-existing item is searched for
 */

const Routes = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [snacks, setSnacks] = useState([]);
  const [drinks, setDrinks] = useState([]);

  async function getSnacksAndDrinks() {
    let snacks = await SnackOrBoozeApi.getSnacks();
    let drinks = await SnackOrBoozeApi.getDrinks();
    setSnacks(snacks);
    setDrinks(drinks);
    setIsLoading(false);
  }
  useEffect(() => {
    getSnacksAndDrinks();
  }, []);

  if (isLoading) {
    return <p>Loading &hellip;</p>;
  }
  return (
    <main>
      <Switch>
        <Route exact path="/">
          <Home snacks={snacks} drinks={drinks} />
        </Route>
        <Route exact path="/snacks/new">
          <AddMenuItemForm itemType="snacks" />
        </Route>
        <Route exact path="/drinks/new">
          <AddMenuItemForm itemType="drinks" />
        </Route>
        <Route exact path="/drinks/:id">
          <BarItem items={drinks} cantFind="/drinks" />
        </Route>
        <Route exact path="/snacks/:id">
          <BarItem items={snacks} cantFind="/snacks" />
        </Route>
        <Route exact path="/drinks">
          <Menu menuItem={drinks} title="Drinks" />
        </Route>
        <Route exact path="/snacks">
          <Menu menuItem={snacks} title="Snacks" />
        </Route>

        <Route>
          <NotFoundPage404 />
        </Route>
      </Switch>
    </main>
  );
};

export default Routes;
