import axios from "axios";
const API_BASE_URL = "https://the-rosary-api.vercel.app";
const CALENDAR_BASE_URL =
  "http://calapi.inadiutorium.cz/api/v0/en/calendars/default";
axios.defaults.withCredentials = false;

class Rosary {
  /**MM/DD/YY */
  static async getByDay() {
    axios.defaults.withCredentials = false;

    // const res = await axios
    //   .get(`${CALENDAR_BASE_URL}/2023/11/29`)
    //   .then(async (res) => await axios.get(`${API_BASE_URL}/v1/wednesday`));
    // return res;
    try {
      const dateResponse = await axios.get(`${CALENDAR_BASE_URL}/2023/11/29`, {
        withCredentials: true,
      });
      const weekday = dateResponse.data.weekday;
      console.log(weekday);

      const res = await axios.get(`${API_BASE_URL}/v1/${weekday}`, {
        withCredentials: true,
      });
      console.log(res);

      return dateResponse;
    } catch (error) {
      console.error("Error in getByDay:", error);
      throw error; // rethrow the error to be caught by the calling code
    }
  }
  static async getByDate(date) {
    try {
      const result = await axios.get(`${API_BASE_URL}/v1/date/${date}`, {
        "access-control-allow-origin": "*",
      });

      // const day = await this.getByDay("tuesday");
      // console.log(day);

      console.log(result);
      return result;
    } catch (err) {
      throw new Error(err);
    }

    // const res = await this.getByDay(result.data.rosary_day);
    // console.log(res);
    // return res;
  }
}
export default Rosary;
