import React, { useState } from "react";
import { useAuth } from "../provider/authProvider";
import { useNavigate } from "react-router-dom";
import AuthForm from "../forms/AuthForm";
import Authorization from "../models/authUser";

const Login = () => {
  const { setToken } = useAuth();
  const navigate = useNavigate();
  // const newToken = await Authorization.login(
  //   userData.username,
  //   userData.password
  // );
  const INITIAL_STATE = {
    username: "",
    password: "",
  };
  const [userData, setUserData] = useState(INITIAL_STATE);

  const handleLogin = async (fData) => {
    setUserData(() => fData);
    setToken(await Authorization.login(fData.username, fData.password));
    login();
  };
  const getToken = async (u, p) => {
    console.log(u, p);
    const newToken = await Authorization.login(u, p);
    setToken(newToken);
  };
  const login = () => {
    navigate("/", { replace: true });
    navigate(0);
  };

  return (
    <>
      <h1>Log in </h1>
      <AuthForm login={login} />
    </>
  );
};

export default Login;
